def ANDequalOR( n: int, arr: List[int]) -> int:
    ans = 0
    pre = 0
    count = 0
    for i in range(n):
        if arr[i] == arr[pre]:
            count += 1
        else:
            ans += (count * (count + 1)) // 2
            pre = i
            count = 1
    ans += (count * (count + 1)) // 2
    return ans

reference_code:
long long ANDequalOR(int n, vector<int> arr) {
    long long ans = 0;
    for (int i = 0; i < n; i++) {
        int pt = i;
        long long ct = 0;
        while (pt < n) {
            if (arr[pt] == arr[i]) {
                pt++;
                ct++;
            } else
                break;
        }
        ct *= (ct + 1);
        ct /= 2;
        ans += ct;
        i = pt - 1;
    }
    return ans;
}

