int MinSize(vector<int> A, int n) {


    int curr = A[0];
    int count = 1;
    for (int i = 1; i < n; ++i) {
        if (A[i] == curr) {
            count += 1;
        } else {
            count -= 1;
            if (count == 0) {
                curr = A[i];
                count = 1;
            }
        }
    }
    int max_f = 0;
    for (int i = 0; i < n; i++)
        if (curr == A[i])
            max_f++;
    if (max_f <= (n + 1) / 2) {
        return n & 1;
    } else {
        return 2 * max_f - n;
    }
}

