def findGoodPairs( a, n, k):
    mp=dict()
    for i in range(n):
        if (a[i] in mp):
            mp[a[i]].append(i)
        else:
            mp[a[i]]=[i]
    c=0
    for i in mp:
        li=mp[i]
        le=len(li)
        i=0
        j=0
        while(j<le):
            if (li[j]-li[i])>=k:
                c+=(le-j)
                i+=1
            else:
                j+=1
    return c

