long long findGoodPairs(vector<int> a, int n, int k) {
    map<int, int > ma;
    long long ans = 0;
    for (int i = k; i < n; i++) {
        ma[a[i - k]]++;
        ans += ma[a[i]];
    }
    return ans;
}

