def trafficLights( n: int, q: int, queries: List[List[int]]) -> str:
    sweap = [0] * (n + 1)
    for l, r in queries:
        sweap[l - 1] += 1
        sweap[r] -= 1
    curr = 0
    ans = ""
    for i in range(n):
        curr = (curr + sweap[i]) % 3
        if curr == 0:
            ans += 'R'
        elif curr == 1:
            ans += 'Y'
        else:
            ans += 'G'
    return ans

