string trafficLights(int n, int q, vector<vector<int>>& queries) {

    vector<int> darr(n + 1, 0);
    for (vector<int>& Q : queries) {
        int a = Q[0];
        int b = Q[1];
        darr[a - 1]++;
        darr[b]--;
    }
    partial_sum(darr.begin(), darr.end(), darr.begin());
    string result = "";
    for (int i = 0; i < n; i++) {
        int X = darr[i] % 3;
        if (X == 0)
            result += "R";
        else if (X == 1)
            result += "Y";
        else if (X == 2)
            result += "G";
    }
    return result;
}

