def smallestString( n : int, s : str) -> str:

    if n == 1:
        return "-1"
    answer = list(s)

    for i in range(n // 2):

        if s[i] != 'a':
            answer[i] = 'a'
            return ''.join(answer)

    answer[-1] = 'b'
    return ''.join(answer)

