def countOperations( s: str) -> int:
    mod = 10**9 + 7
    ans = 0
    count_b = 0
    for i in range(len(s) - 1, -1, -1):
        if s[i] == 'b':
            count_b = (count_b + 1) % mod
        else:
            ans = (ans + count_b) % mod
            count_b = (count_b + count_b) % mod
    return ans

