int countOperations(string s) {
    const int mod = 1000000007;
    int ans = 0;
    int count_b = 0;
    for (int i = s.length() - 1; i >= 0; --i) {
        if (s[i] == 'b')
            count_b = (count_b + 1) % mod;
        else {
            ans = (ans + count_b) % mod;
            count_b = (count_b + count_b) % mod;
        }
    }
    return ans;
}

