int areaOfMaxDiagonal(vector<vector<int>> &dimensions) {
    int ans = 0, max_l = 0;
    for (auto &d: dimensions) {
        int x = d[0], y = d[1];
        int l = x * x + y * y;
        if (l > max_l || (l == max_l && x * y > ans)) {
            max_l = l;
            ans = x * y;
        }
    }
    return ans;
}

