string compressedString(string word) {
    string t;
    int i0 = -1;
    for (int i = 0; i < word.length(); i++) {
        char c = word[i];
        if (i + 1 == word.length() || c != word[i + 1]) {
            int k = i - i0;
            for (int j = 0; j < k / 9; j++) {
                t += '9';
                t += c;
            }
            if (k % 9) {
                t += '0' + (k % 9);
                t += c;
            }
            i0 = i;
        }
    }
    return t;
}

