def minimumTime( nums1: List[int], nums2: List[int], x: int) -> int:
    n = len(nums1)
    dp = [[0] * (n + 1) for _ in range(n + 1)]
    for j, (b, a) in enumerate(sorted(zip(nums2, nums1)), 1):
        for i in range(j, 0, -1):
            dp[j][i] = max(dp[j - 1][i], dp[j - 1][i - 1] + i * b + a)
    sa, sb = sum(nums1), sum(nums2)
    for i in range(0, n + 1):
        if sb * i + sa - dp[n][i] <= x:
            return i
    return -1

reference_code:
int minimumTime(vector<int>& nums1, vector<int>& nums2, int x) {
    int n = nums1.size();
    vector<vector<int>> dp(n + 1, vector<int>(n + 1, 0));
    vector<pair<int, int>> nums(n);
    for (int i = 0; i < n; i++) {
        nums[i] = {nums2[i], nums1[i]};
    }
    sort(nums.begin(), nums.end());
    for (int j = 1; j <= n; j++) {
        int b = nums[j - 1].first, a = nums[j - 1].second;
        for (int i = j; i > 0; i--) {
            dp[j][i] = max(dp[j - 1][i], dp[j - 1][i - 1] + i * b + a);
        }
    }
    int s1 = accumulate(nums1.begin(), nums1.end(), 0);
    int s2 = accumulate(nums2.begin(), nums2.end(), 0);
    for (int i = 0; i <= n; i++) {
        if (s2 * i + s1 - dp[n][i] <= x) {
            return i;
        }
    }
    return -1;
}

