int chocolateDistribution(int N, int M, vector<int> &arr, vector<int> &brr) {
    sort(arr.begin(), arr.end());
    sort(brr.begin(), brr.end());
    int ans = 0;
    int i = 0, j = 0;
    while (i < N && j < M) {
        if (arr[i] <= brr[j]) {
            ans++;
            i++;
            j++;
        } else {
            j++;
        }
    }
    return ans;
}

