def frequency( n ,a):
    mp=dict()
    for i in range(n):
        if a[i] in mp:
            mp[a[i]]+=1
        else:
            mp[a[i]]=1
    ans=[]
    for i in range(n):
        ans.append(mp[a[i]])
        mp[a[i]]-=1
    return ans

