vector<int> frequency(int n, vector<int> &a){
    vector<int>ans(n);
    unordered_map<int,int>mp;
    for(int i=n-1;i>-1;i--)
    {
        mp[a[i]]++;
        ans[i]=mp[a[i]];
    }
    return ans;
}

