def getSmallestString2( s: str, k: int) -> str:
    s = list(s)
    for i, c in enumerate(map(ord, s)):
        dis = min(c - ord('a'), ord('z') - c + 1)
        if dis > k:
            s[i] = chr(c - k)
            break
        s[i] = 'a'
        k -= dis
    return ''.join(s)

