string getSmallestString2(string s, int k) {
    for (int i = 0; i < s.length(); i++) {
        int dis = min(s[i] - 'a', 'z' - s[i] + 1);
        if (dis > k) {
            s[i] -= k;
            break;
        }
        s[i] = 'a';
        k -= dis;
    }
    return s;
}

