def BeautifulBits( n : int, arr : List[int], q : int, queries : List[List[int]]) -> List[int]:
    pref = [[0] * 32 for _ in range(n + 1)]
    for i in range(1, n + 1):
        for j in range(32):
            pref[i][j] = pref[i - 1][j] + ((arr[i - 1] >> j) & 1)
    ans = [0] * q
    for i in range(q):
        for j in range(32):
            if pref[queries[i][1]][j] - pref[queries[i][0] - 1][j] != 0:
                ans[i] += 1 << j
    return ans

