vector<int> BeautifulBits(int n, vector<int> &arr, int q,
                          vector<vector<int> > &queries) {
    vector<vector<long long> > pref(n + 1, vector<long long>(32, 0));
    for (int i = 1; i <= n; i++) {
        for (int j = 0; j < 32; j++) {
            pref[i][j] = pref[i - 1][j] + ((arr[i - 1] >> j) & 1);
        }
    }
    vector<int> ans(q, 0);
    for (int i = 0; i < q; i++) {
        for (int j = 0; j < 32; j++) {
            if (pref[queries[i][1]][j] - pref[queries[i][0] - 1][j] != 0) {
                ans[i] += 1 << j;
            }
        }
    }
    return ans;
}

