long long maxAlternatingSubstring(int N, string S) {
    vector<long long> diff;
    for (int i = 0; i < N;) {
        int j = i + 1;
        while (j < N && S[j] != S[j - 1])
            j++;
        diff.push_back(j - i);
        i = j;
    }
    diff.push_back(0);
    long long ans_before = 0;
    for (long long x : diff) {
        ans_before += ((x * (x + 1)) / 2);
    }
    long long ans = 0;
    for (int i = 0; i < diff.size() - 1; ++i) {
        long long a = diff[i];
        long long b = diff[i + 1];
        long long currAns = ans_before - ((a * (a + 1)) / 2) - ((b * (b + 1)) / 2) +
                            ((a + b + 1) * (a + b + 2)) / 2;
        ans = max(ans, currAns);
    }
    return ans;
}

