def canTrade( n: int, beautifulness: List[int], q: int,
             queries: List[List[int]]) -> List[int]:
    par = [i for i in range(n)]
    for i in range(1, n):
        if beautifulness[i] == beautifulness[i - 1]:
            par[i] = par[i - 1]
        else:
            pass
    ans = []
    for el in queries:
        a, b = el[0] - 1, el[1] - 1
        if par[a] == par[b]:
            ans.append(1)
        else:
            ans.append(0)
    return ans

