def findFun( n : int) -> int:
    dp = [2,5,9,7]
    if n<=3:
        return dp[n]
    mod = 10**9+7
    for i in range(4,n+1):
        if i&1==0:
            z = dp[2]*(i-2) + dp[0]*(i-4)
        else:
            z = dp[3]*(i-1) + dp[1]*(i-3)
        z = z%mod  
        dp[0] = dp[1]
        dp[1] = dp[2]
        dp[2] = dp[3]
        dp[3] = z
    return dp[-1]

