    long long p1=2;
    long long p2=5;
    long long p3=9;
    long long p4=7;
    for (int i=4;i<=n;i++){
        if (i%2==0){
            int a=(p3*(i-2))%mod;
            int b=(p1*(i-4))%mod;
            int ele=(a+b)%mod;
            p1=p2;
            p2=p3;
            p3=p4;
            p4=ele;
        }
        else{
            int a=(p4*(i-1))%mod;
            int b=(p2*(i-3))%mod;
            int ele=(a+b)%mod;
            p1=p2;
            p2=p3;
            p3=p4;
            p4=ele;
        }
    }
    return p4;
}

