def CompatibleStrings( N : int, A1 : List[str], A2 : List[str]) -> List[int]:
    ans = [0]*N
    for i in range(N):
        s1 = A1[i]
        s2 = A2[i]
        if len(s1)!=len(s2):
            continue
        mp1 = Counter(s1)
        mp2 = Counter(s2)
        f = True
        c2 = 0
        c3 = 0
        g = 0
        r = 0
        for c in range(97,123):
            c = chr(c)
            if mp1[c]==mp2[c]:
                continue
            d = abs(mp1[c]-mp2[c])
            if mp2[c]>mp1[c]:
                if r<d:
                    f = False
                    break
                else:
                    r-=d
            else:
                r += d
            g = gcd(g,d)
            if g==1:
                f = False
                break
        if f:
            ans[i] = 1
    return ans

