def createString( n : int, k : int, s : str) -> str:
    c=Counter(s)
    t=[(c[el],el) for el in c]
    t.sort(key=lambda x:(x[1]))
    ans=""
    while t:
        ca,cb=t.pop()
        if ca>k:
            ca-=k
            ans+=cb*k
            if len(t)==0:
                return ans
            else:
                da,db=t.pop()
                if da>1:
                    ans+=db
                    t.append((da-1,db))
                else:
                    ans+=db
            t.append((ca,cb))
        else:
            ans+=cb*ca
    return ans

