string createString(int n, int k, string s) {
    string answer = "";
    unordered_map<char, int> m;
    for(auto i : s){
        m[i] += 1;
    }
    set<pair<char, int>> st;
    for(auto i : m){
        st.insert({i.first, i.second});
    }
    int curr = 0;
    while(st.size()){
        auto largest = st.end();
        largest--;
        if(curr == k){
            if(st.size() == 1){
                break;
            }else{
                largest--;
            }
            curr = -1;
        }
        char now = largest->first;
        int count = largest->second;
        st.erase({now, count});
        count -= 1;
        if(count){
            st.insert({now, count});
            curr += 1;
        }else{
            curr = 0;
        }
        answer += now;
    }
    return answer;
}

