int countSuperPalindrome(int l, int r) {
    int count = 0;

    int start = static_cast<int>(ceil(sqrt(l)));

    while (start * start <= r) {

        int square = start * start;
        string s = to_string(square);

        if (s == string(s.rbegin(), s.rend())) {
            count++;
        }
        start++;
    }
    return count;
}

