int minimumOperationsToMakeKPeriodic(string word, int k) {
    int n = word.size(), mx = 0;
    unordered_map<string, int> cnt;
    for (int i = k; i <= n; i += k) {
        mx = max(mx, ++cnt[word.substr(i - k, k)]);
    }
    return n / k - mx;
}

