int Towerheight(int n, int k, vector<int>& a) {

    stack<int> st;
    int totalHeight = 0;
    int i = 0;

    for (int j = 0; j < n; j++) {
        totalHeight += a[j];
        st.push(a[j]);

        if (totalHeight > k) {

            int removeBoxes = i + 1;
            while (removeBoxes > 0 && !st.empty()) {
                totalHeight -= st.top();
                st.pop();
                removeBoxes--;
            }
            i++;
        }
    }

    return totalHeight;
}

