def arrangingHorses( n: int, m: int,
                    data: List[List[int]]) -> List[int]:
    deg = [0] * (n)
    g = [[] for _ in range(n)]
    for el in data:
        deg[el[0]] += 1
        g[el[1]].append(el[0])
    q = []
    ans = []
    for i in range(n):
        if deg[i] == 0:
            heappush(q, i)
    while q:
        node = heappop(q)
        ans.append(node)
        for el in g[node]:
            deg[el] -= 1
            if deg[el] == 0:
                heappush(q, el)
    return ans

