vector<int> arrangingHorses(int n, int m, vector<vector<int>>& data) {
    vector<int> inDegree(n, 0);
    vector<vector<int>> adjList(n);
    for (const auto& edge : data) {
        int a = edge[0], b = edge[1];
        adjList[b].push_back(a); 
        ++inDegree[a];
    }
    priority_queue<int, vector<int>, greater<int>> pq; 
    for (int i = 0; i < n; ++i) {
        if (inDegree[i] == 0)
            pq.push(i);
    }
    vector<int> order;
    while (!pq.empty()) {
        int horse = pq.top();
        pq.pop();
        order.push_back(horse);
        for (int neigh : adjList[horse]) {
            --inDegree[neigh];
            if (inDegree[neigh] == 0)
                pq.push(neigh);
        }
    }
    return order;
}

