def maxResult( nums: List[int], k: int) -> int:
    n = len(nums)
    dp = [0] * n
    dp[0] = nums[0]
    queue = deque([0])
    for i in range(1, n):
        while queue and queue[0] < i - k:
            queue.popleft()
        dp[i] = dp[queue[0]] + nums[i]
        while queue and dp[queue[-1]] <= dp[i]:
            queue.pop()
        queue.append(i)
    return dp[n - 1]

reference_code:
int maxResult(vector<int>& nums, int k) {
    int n = nums.size();
    vector<int> dp(n);
    dp[0] = nums[0];
    deque<int> queue;
    queue.push_back(0);
    for (int i = 1; i < n; i++) {
        while (!queue.empty() && queue.front() < i - k) {
            queue.pop_front();
        }
        dp[i] = dp[queue.front()] + nums[i];
        while (!queue.empty() && dp[queue.back()] <= dp[i]) {
            queue.pop_back();
        }
        queue.push_back(i);
    }
    return dp[n - 1];
}

