def maxmiumScore( cards: List[int], cnt: int) -> int:
        cards.sort(reverse=True)
        ans = 0
        tmp = 0
        odd = even = -1
        end = len(cards) - cnt
        for i in range(cnt):
            tmp += cards[i]
            if cards[i] % 2 == 1:
                odd = cards[i]
            else:
                even = cards[i]
        if tmp % 2 == 0:
            return tmp
        for i in range(cnt, len(cards)):
