def circularGameLosers( n: int, k: int) -> List[int]:
        visit = [False] * n
        i = k
        j = 0
        while not visit[j]:
            visit[j] = True
            j = (j + i) % n
            i += k
        ans = []
        for i in range(n):
            if not visit[i]:
                ans.append(i + 1)
        return ans

reference_code:
vector<int> circularGameLosers(int n, int k) {
        vector<bool> visit(n, false);
        for (int i = k, j = 0; !visit[j]; i += k) {
            visit[j] = true;
            j = (j + i) % n;
        }
        vector<int> ans;
        for (int i = 0; i < n; i++) {
            if (!visit[i]) {
                ans.emplace_back(i + 1);
            }
        }
        return ans;
    }

