def countOfPairs( nums):
        MOD = 1_000_000_007
        m = len(nums)
        n = max(nums)
        f = [0] * (n + 1)

        for v in range(nums[0] + 1):
            f[v] = 1
        preSums = []

        for i in range(1, m + 1):

            preSums.clear()
            preSum = 0
            for v in f:
                preSum += v
                preSum %= MOD
                preSums.append(preSum)

            if i == m:
                return preSums[-1]

            for j in range(nums[i] + 1):
                ceil = min(j, nums[i-1] - nums[i] + j)
                if ceil >= 0:
                    f[j] = preSums[ceil]
                else:  
                    f[j] = 0
            for j in range(nums[i] + 1, len(f)):
                f[j] = 0

        return 0

