def minFlips( a: List[List[int]]) -> int:
        ans = 0
        m, n = len(a), len(a[0])
        for i in range(m // 2):
            row, row2 = a[i], a[-1 - i]
            for j in range(n // 2):
                cnt1 = row[j] + row[-1 - j] + row2[j] + row2[-1 - j]
                ans += min(cnt1, 4 - cnt1)  
        if m % 2 and n % 2:

            ans += a[m // 2][n // 2]
        diff = cnt1 = 0
        if m % 2:

            row = a[m // 2]
            for j in range(n // 2):
                if row[j] != row[-1 - j]:
                    diff += 1
                else:
                    cnt1 += row[j] * 2
        if n % 2:

            for i in range(m // 2):
                if a[i][n // 2] != a[- 1 - i][n // 2]:
                    diff += 1
                else:
                    cnt1 += a[i][n // 2] * 2
        return ans + (diff if diff else cnt1 % 4)

