def numberOfSubstrings( s: str) -> int:
        n = len(s)
        a = [i for i, b in enumerate(s) if b == '0']
        tot1 = n - len(a)
        a.append(n)  
        ans = i = 0  
        for left, b in enumerate(s):
            if b == '1':
                ans += a[i] - left  
            for k in range(i, len(a) - 1):
                cnt0 = k - i + 1
                if cnt0 * cnt0 > tot1:
                    break
                cnt1 = a[k] - left - (k - i)

                ans += max(a[k + 1] - a[k] - max(cnt0 * cnt0 - cnt1, 0), 0)
            if b == '0':
                i += 1  
        return ans

