int numberOfSubstrings(string s) {
        int n = s.length();
        vector<int> a;
        for (int i = 0; i < n; i++) {
            if (s[i] == '0') {
                a.push_back(i);
            }
        }
        int tot1 = n - a.size();
        a.push_back(n); 
        int ans = 0, i = 0; 
        for (int left = 0; left < n; left++) {
            if (s[left] == '1') {
                ans += a[i] - left; 
            }
            for (int k = i; k < a.size() - 1; k++) {
                int cnt0 = k - i + 1;
                if (cnt0 * cnt0 > tot1) {
                    break;
                }
                int cnt1 = a[k] - left - (k - i);
                ans += max(a[k + 1] - a[k] - max(cnt0 * cnt0 - cnt1, 0), 0);
            }
            if (s[left] == '0') {
                i++; 
            }
        }
        return ans;
    }

