def countUniques( a, k, l, r):

        set_ = set()
        dup = 0
        for i in a:
            if i in set_:
                dup += 1
            else:
                set_.add(i)
        unique = len(set_)
        for i in range(l, r + 1):
            if k > 0 and dup > 0 and i not in set_:
                unique += 1
                k -= 1
                dup -= 1
        return unique

reference_code:
int countUniques(vector<int> a, int k, int l, int r) {

        unordered_set<int> set;
        int dup = 0;
        for (int i : a) {
            if (!set.insert(i).second) {
                dup++;
            }
        }
        int unique = set.size();
        for (int i = l; i <= r; i++) {
            if (k > 0 && dup > 0 && set.find(i) == set.end()) {
                unique++;
                k--;
                dup--;
            }
        }
        return unique;
    }

