vector<vector<int>> findEnemies(int n, int m, int k, vector<int> &col, vector<int> &count) {
    vector<vector<int>> answer(n, vector<int>(m, -1));
    int curr = 0;
    int ind = 0;
    while(curr < k && ind < n){
        int total = count[ind];
        while(total--){
            answer[ind][col[curr]] = curr;
            curr += 1;
        }
        ind += 1;
    }
    return answer;
}

