def minOrAfterOperations( nums: List[int], k: int) -> int:
    ans = mask = 0
    for b in range(max(nums).bit_length() - 1, -1, -1):
        mask |= 1 << b
        cnt = 0  
        and_res = -1  
        for x in nums:
            and_res &= x & mask
            if and_res:
                cnt += 1  
            else:
                and_res = -1  
        if cnt > k:
            ans |= 1 << b  
            mask ^= 1 << b  
    return ans

