long long ForYou(int n, vector<int> &arr, int m, vector<vector<int> > &order)
{
    vector<int> diff(n+1,0);
    for(auto it:order)
    {
        diff[it[0]-1]++;
        diff[it[1]]--;
    }
    for(int i=1;i<=n;i++)
    {
        diff[i] += diff[i-1];
    }
    diff.pop_back();
    sort(diff.begin(),diff.end());
    sort(arr.begin(),arr.end());
    long long ans=0;
    int mod = 1e9+7;
    for(int i=0;i<n;i++)
    {
        ans += (1ll * diff[i]) * (1ll * arr[i]);
        ans%=mod;
    }
    return ans;
}

