def maximumBattalions( N : int, names : List[str]) -> int:
    d={}
    for i in range(N):
        if names[i] not in d:
            d[names[i]]=[]
        d[names[i]].append(i)
    seen=set()
    ans=0
    for i in range(N):
        if names[i] not in seen:
            seen.add(names[i])
        if d[names[i]][-1]==i:
            seen.remove(names[i])
        if len(seen)==0:
            ans+=1
    return ans

