    unordered_map<int, array<int, 26>> map;
    vector<int> ans(n);
    for (int i = n - 1; i >= 0; i--) {
        string x = X[i];
        int cans = 0;
        for (int j = 0; j < x.length(); j++) {
            if (map.find(j) == map.end()) {
                map[j] = array<int, 26>{};
            }
            cans += map[j][x[j] - 'a'];
            map[j][x[j] - 'a']++;
        }
        ans[i] = cans;
    }
    return ans;
}

