int candyShop(int n, vector<int> &candies) {
    int answer = INT_MAX;
    unordered_map<int, int> m;
    for(auto i : candies){
        m[i] += 1;
    }
    int k = m.size();
    m.clear();
    int l = 0, r = 0;
    int total = 0;
    while(r < n){
        m[candies[r]] += 1;
        total += candies[r];
        while(m[candies[l]] > 1){
            m[candies[l]] -= 1;
            total -= candies[l];
            l += 1;
        }
        if(m.size() == k){
            answer = min(answer, total);
        }
        r += 1;
    }
    return answer;
}

