def minimizeArr( n: int, arr: List[int]) -> int:
    min_element = min(arr)
    if arr.count(min_element) == 1:
        return 1
    else:
        count = arr.count(min_element)
        for el in arr:
            if el % min_element != 0:
                return 1
        return (count + 1) // 2

