int minimizeArr(int n, vector<int>&arr){
    int mi=INT_MAX;
    for (auto j:arr) mi=min(mi,j);
    int ct=0;
    for (auto j:arr){
        if (j==mi)ct+=1;
    }
    if (ct==1) return 1;
    for (auto j:arr){
        if (j%mi!=0) return 1;
    }
    return ceil((float)ct / 2);
}

