int minimumLevels(vector<int> &possible) {

    int n = possible.size();
    int s = accumulate(possible.begin(), possible.end(), 0) * 2 - n;
    int pre = 0;
    for (int i = 0; i < n - 1; i++) {
        pre += possible[i] ? 2 : -2;
        if (pre > s) {
            return i + 1;
        }
    }
    return -1;
}

