        for (int x = 1; x <= n; x++) {
            if (nums[x] == 0) {
                continue;
            }
            for (int i = 1; i <= n; i++) {
                if (x % i == 1) {
                    nums[i] = 1;
                }
            }
        }
    }
    return accumulate(nums.begin(), nums.end(), 0);
}

