    for (int j = 0; j < m; j++) {
        int st = 0;
        for (int i = 0; i < n; i++) {
            st |= (grid[j][i] << i);
        }
        mp[st] = j;
    }
    if (mp.count(0)) {
        ans.push_back(mp[0]);
        return ans;
